 editorUsesEnterKey=&quot;false|true&quot;
 editorWidthOffset=&quot;0&quot;
 editorXOffset=&quot;0&quot;
 editorYOffset=&quot;0&quot;
 formatter=&quot;null&quot;
 headerRenderer=&quot;AdvancedDataGridHeaderRenderer&quot;
 headerText=&quot;<i>No default</i>&quot;
 headerWordWrap=&quot;undefined&quot;
 imeMode=&quot;null&quot;
 itemEditor=&quot;TextInput&quot;
 itemRenderer=&quot;AdvancedDataGridItemRenderer&quot;
