 resizable=&quot;true|false&quot;
 showDataTips=&quot;false|true&quot;
 sortable=&quot;true|false&quot;
 sortCompareFunction=&quot;<i>No default</i>&quot;
 sortDescending=&quot;false|true&quot;
 styleFunction=&quot;<i>No default</i>&quot;
 visible=&quot;true|false&quot;
 width=&quot;100&quot;
 wordWrap=&quot;false|true&quot;

 <b>Styles</b>
 backgroundColor=&quot;0xFFFFFF&quot;
 color=&quot;<i>No default.</i>&quot;
